import * as Icons from "../public/icons/_compiled"
import Button from "../components/UI/Button"
import Card from "../components/UI/Card"
import Carousel from "nuka-carousel"
import Link from "next/link"

export default function index() {

    const btnClick = (): void => {
        console.log("cliks")
    }
    const previousButton = ({ previousSlide }: any) => (
        <Icons.ArrowRight style={{cursor: "pointer"}} onClick={previousSlide}/>
    )

    const nextButtom = ({ nextSlide }: any) => (
        <Icons.ArrowLeft style={{cursor: "pointer"}} onClick={nextSlide}/>
    )

    return (
        <>
            <div>
                <div className="header">
                    <div className="navbar">
                        <div className="logo">Ã¤lem</div>
                        <div className="categories">
                            <div className="men">Men</div>
                            <div className="women">Women</div>
                        </div>
                        <div className="actions">
                            <div className="search">
                                <Icons.Search />
                            </div>
                            <div className="search">
                                <Icons.Cart />
                            </div>
                            <div className="search">
                                <Icons.Avatar />
                            </div>
                        </div>
                    </div>
                    <div className="header-main">
                        <div className="title">Brand new January Collection</div>
                        <Button
                            customStyleObject={{ marginTop: "40px" }}
                            backgroundColor="#ff7070"
                            onClick={btnClick}
                            content="Check More"
                            borderRadius="30px"
                            height="50px"
                            width="150px"
                            border={false}
                            color="#fff"
                        />
                    </div>
                    {/* carousel */}
                </div>
                <div className="main">
                    <div className="promocards">
                        <Card
                            height="375px"
                            width="650px"
                            backgroundColor="black"
                            title="New arrivals are now in!"
                            color="#fff"
                            fontSize="30px"
                            actionButton={<Button
                                content="Show Collection"
                                color="#fff"
                                backgroundColor="#ff7070"
                                borderRadius="30px"
                                height="50px"
                                width="150px"
                            />
                            }
                        />
                        <Card
                            height="375px"
                            width="350px"
                            title="Jackets"
                            subTitle="$ 39.99"
                            backgroundColor="purple"
                            color="#fff"
                            fontSize="30px"
                            actionButton={<Button
                                content="Show All"
                                color="#fff"
                                backgroundColor="#ff7070"
                                borderRadius="30px"
                                height="50px"
                                width="150px"
                            />
                            }
                        />
                        <Card
                            height="375px"
                            width="350px"
                            title="Sale This Winter -50%"
                            backgroundColor="purple"
                            color="#fff"
                            fontSize="30px"
                        />
                    </div>
                    <div className="popular">
                        <div className="popular-header">
                            <div className="popular-title">Popular</div>
                            <Button
                                content="Show More"
                                color="#fff"
                                backgroundColor="#ff7070"
                                borderRadius="30px"
                                height="50px"
                                width="120px"
                            />
                        </div>
                        <Carousel
                            className="carousel"
                            height="450px"
                            slideWidth="340px"
                            // width={`${windowWidth * 0.89 - 260}px`}
                            cellSpacing={40}
                            wrapAround={false}
                            renderCenterLeftControls={previousButton}
                            renderCenterRightControls={nextButtom}

                        >
                            {[0, 1, 2, 3, 4].map((i) => (
                                <div className="card" key={i}>
                                    <Card
                                        height="375px"
                                        width="350px"
                                        backgroundColor="purple"
                                        color="#fff"
                                        fontSize="30px"
                                    />
                                    <div className="product-info">
                                        <div className="product-name">Jacket</div>
                                        <div className="product-price">
                                            <div className="new-price">$21.99</div>
                                            <div className="old-price" style={{
                                                textDecoration: "line-through",
                                            }}>$39.99</div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </Carousel>
                    </div>
                    <div className="pros-cards">
                        <div className="pros-header">Why you should choose us?</div>
                    </div>
                </div>
            </div>
            <style jsx>{`
                .header{
                    background: grey;
                    height: 750px
                }
                .navbar{
                    display: flex;
                    justify-content: space-between;
                    margin-left: 170px;
                    margin-right: 170px;
                    padding-top: 40px
                }
                .categories{
                    display: flex;
                    width: 100px;
                    justify-content: space-between;
                }
                .actions{
                    display: flex;
                    width: 120px;
                    justify-content: space-between;
                }
                .search{
                    cursor: pointer
                }
                .header-main{
                    margin-left: 210px;
                    margin-top: 80px
                }
                .title{
                    font-size: 50px;
                    width: 300px
                }
                .main{
                    margin-left: 170px;
                    margin-right: 170px;
                }
                .promocards{
                    margin-top: 40px;
                    display: flex;
                    justify-content: space-between
                }
                .popular-header{
                    margin-top: 80px;
                    display: flex;
                    justify-content: space-between;
                    margin-bottom: 60px;
                }
                .popular-title{
                    font-size: 29px
                }
            `}</style>
        </>
    )
}
