import React from 'react'

namespace Btn {
    export interface Props {
        children?: React.ReactNode
        onMouseEnter?: () => void
        onMouseLeave?: () => void
        [propName: string]: any
        size?: "big" | "small"
        borderRadius?: string
        onClick?: () => void
        focusable?: boolean
        className?: string
        content?: string
        height?: string
        backgroundColor?: string
        width?: string
        base?: boolean
    }
}

export default function Button(props: Btn.Props) {

    return (
        <>
            <div
                className={props.className + " btn-main"}
                onClick={props.onClick}
                onMouseEnter={props.onMouseEnter}
                onMouseLeave={props.onMouseLeave}
            >
                {props.content}
            </div>
            <style jsx>{`
                .btn-main{
                    height: ${props.height ? props.height : "40px"};
                    width: ${props.width ? props.width : "150px"};
                    background-color: ${props.backgroundColor ? props.backgroundColor : "grey"};
                    border: ${props.base ? `1px solid ${props.backgroundColor}`: "none"};
                    border-radius: ${props.borderRadius ? props.borderRadius : "3px"};
                    cursor: pointer
                }
            `}</style>
        </>
    )
}
