import React, { ReactElement, useRef, useEffect, useState } from "react"

namespace CardNamespace {
    export interface Props {
        customStyleObject?: object
        // children?: React.ReactNode
        onMouseEnter?: () => void
        onMouseLeave?: () => void
        backgroundColor?: string
        [propName: string]: any
        borderRadius?: string
        onClick?: () => void
        className?: string
        height?: string
        width?: string
        header?: string
        image?: { path: string, position: "top" | "bottom" | "left" | "right" }
        border?: boolean
        borderColor?: string
        bgImage?: string
        textPosition?: "center" | "left" | "right",
        title?: string
        subTitle?: string
        text?: string
    }
}

export default function Card(props: CardNamespace.Props) {
    const cardRef: any = useRef()
    const [cardHeight, setHeight] = useState()

    useEffect(() => {
        setHeight(cardRef.current.getBoundingClientRect().height)
    }, [])

    const headerStyle = {
        marginLeft: props.textPosition === "left" ? "10px" : "",
        marginRight: props.textPosition === "right" ? "10px" : "",
        marginTop: "5px",
        textAlign: props.textPosition ? props.textPosition : "center",
    }

    const imageStyle = {
        backgroundImage: props.image ? "url(" + props.image.path + ")" : "",
        backgroundPosition: "center center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        height: `${cardHeight / 2.2}px`,
    }

    const cardStyle = {
        height: props.height ? props.height : "250px",
        width: props.width ? props.width : "200px",
        border: props.border ? `1px solid ${props.borderColor ? props.borderColor : "grey"}` : "none",
        backgroundImage: props.bgImage ? "url(" + props.bgImage + ")" : "",
        backgroundPosition: "center center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        backgroundColor: props.backgroundColor ? props.backgroundColor : "#fff",
        borderRadius: props.borderRadius ? props.borderRadius : "3px",
    }

    return (
        <div ref={cardRef} style={cardStyle}>
            {props.header ? <div style={headerStyle}>{props.header}</div> : ""}
            {props.image ? <div style={imageStyle} /> : ""}
            {props.title ? <div style={{
                textAlign: props.textPosition ? props.textPosition : "center",
                fontSize: "16px",
            }}>{props.title}</div> : ""}
            {props.subTitle ? <div style={{
                textAlign: props.textPosition ? props.textPosition : "center",
                fontSize: "14px",
                color: "gray"
            }}>{props.subTitle}</div> : ""}
            {props.text ? <div style={{
                textAlign: props.textPosition ? props.textPosition : "center",
                fontSize: "14px",
            }}>{props.text}</div> : ""}
        </div>
    )
}
