import React, { ReactElement, useRef, useEffect, useState } from "react"
import Button from "./Button"

namespace CardNamespace {
    export interface Props {
        customStyleObject?: object
        onMouseEnter?: () => void
        onMouseLeave?: () => void
        backgroundColor?: string
        [propName: string]: any
        borderRadius?: string
        onClick?: () => void
        className?: string
        height?: string
        width?: string
        header?: string
        image?: { path: string, position: "top" | "bottom" | "left" | "right", height: string }
        border?: boolean
        borderColor?: string
        bgImage?: string
        textPosition?: "center" | "left" | "right",
        title?: string
        subTitle?: string
        text?: string
        actionButton?: React.ReactNode
        footer?: React.ReactNode
        body?: React.ReactNode
    }
}

export default function Card(props: CardNamespace.Props) {
    const cardRef: any = useRef()
    const [cardHeight, setHeight] = useState()

    useEffect(() => {
        setHeight(cardRef.current.getBoundingClientRect().height)

        const Img = props.image ? <div style={imageStyle} /> : ""
        const Header = props.header ? <div className="header" style={headerStyle}>{props.header}</div> : ""
        const Body = <div className="body">
            {props.title ? <div style={titleStyle}>{props.title}</div> : ""}
            {props.subTitle ? <div style={subTitleStyle}>{props.subTitle}</div> : ""}
            {props.text ? <div style={textStyle}>{props.text}</div> : ""}
            {props.body ? <div >{props.body}</div> : ""}
        </div>
        const Btn = props.actionButton ? <div style={btnStyle}>{props.actionButton}</div> : ""
        const Footer = props.footer ? <div style={footerStyle}>{props.footer}</div> : ""

        const Elements = [Header, Body, Btn, Footer]
        if (props.image?.position === "top" && Elements[0] === "") {
            Elements[0] = Img
        }
        if (props.image?.position === "top" && Elements[0] !== "") {
            Elements[1] = (Img)
        }
        console.log(Elements)
    }, [])

    const btnPosition = (textPosition: string) => {
        switch (textPosition) {
            case "center":
                return "center"
            case "left":
                return "flex-start"
            case "right":
                return "flex-end"
            default:
                break
        }
    }

    const commonTextStyle = {
        textAlign: props.textPosition ? props.textPosition : "center",
        marginRight: props.textPosition === "right" ? "10px" : "",
        marginLeft: props.textPosition === "left" ? "10px" : "",
    }
    const headerStyle = {
        ...commonTextStyle,
        marginTop: "5px",
        borderBottom: "1px solid #bfbfbf",
    }

    const titleStyle = {
        ...commonTextStyle,
        marginTop: "5px",
        fontSize: "16px",
    }

    const subTitleStyle = {
        ...commonTextStyle,
        fontSize: "14px",
        color: "gray",
    }

    const textStyle = {
        ...commonTextStyle,
        fontSize: "14px",
        marginTop: "5px",
    }

    const btnStyle = {
        marginTop: "10px",
        display: "flex",
        justifyContent: props.textPosition ? btnPosition(props.textPosition) : "center",
        marginRight: props.textPosition === "right" ? "10px" : "",
        marginLeft: props.textPosition === "left" ? "10px" : "",
        ...commonTextStyle,
    }

    const footerStyle = {
        borderTop: "1px solid #bfbfbf",
        marginTop: "10px",
        ...commonTextStyle,
    }

    const imageStyle = {
        backgroundImage: props.image ? "url(" + props.image.path + ")" : "",
        backgroundPosition: "center center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        height: `${props.image ? props.image.height : cardHeight / 1.6}px`,
    }

    const cardStyle = {
        height: props.height ? props.height : "",
        width: props.width ? props.width : "200px",
        border: props.border ? `1px solid ${props.borderColor ? props.borderColor : "#bfbfbf"}` : "none",
        backgroundImage: props.bgImage ? "url(" + props.bgImage + ")" : "",
        backgroundPosition: "center center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        backgroundColor: props.backgroundColor ? props.backgroundColor : "#fff",
        borderRadius: props.borderRadius ? props.borderRadius : "3px",
        paddingBottom: "10px",
    }

    return (
        <div
            ref={cardRef} style={cardStyle}
            onClick={props.onClick}
            onMouseEnter={props.onMouseEnter}
            onMouseLeave={props.onMouseLeave}
        >

        </div>
    )
}
