import * as Icons from "../public/icons/_compiled"
import Button from "../components/UI/Button"
import Card from "../components/UI/Card"
import Carousel from "nuka-carousel"
import Link from "next/link"

export default function index() {

    const btnClick = (): void => {
        console.log("cliks")
    }
    const previousButton = ({ previousSlide }: any) => (
        <div className="parent" style={{ marginTop: "-100px" }}>
            <Icons.ArrowRight style={{ cursor: "pointer" }} onClick={previousSlide} />
        </div>
    )

    const nextButtom = ({ nextSlide }: any) => (
        <div className="parent" style={{ marginTop: "-100px" }}>
            <Icons.ArrowLeft style={{ cursor: "pointer" }} onClick={nextSlide} />
        </div>
    )

    return (
        <>
            <div>
                <div className="header">
                    <div className="navbar">
                        <div className="logo">älem</div>
                        <div className="categories">
                            <div className="men">Men</div>
                            <div className="women">Women</div>
                        </div>
                        <div className="actions">
                            <div className="search">
                                <Icons.Search />
                            </div>
                            <div className="search">
                                <Icons.Cart />
                            </div>
                            <div className="search">
                                <Icons.Avatar />
                            </div>
                        </div>
                    </div>
                    <div className="header-main">
                        <div className="title">Brand new January Collection</div>
                        <Button
                            customStyleObject={{ marginTop: "40px" }}
                            backgroundColor="#ff7070"
                            onClick={btnClick}
                            content="Check More"
                            borderRadius="30px"
                            height="50px"
                            width="150px"
                            border={false}
                            color="#fff"
                        />
                    </div>
                </div>
                <div className="main">
                    <div className="promocards">
                        <Card
                            height="375px"
                            width="650px"
                            backgroundColor="black"
                            title="New arrivals are now in!"
                            color="#fff"
                            fontSize="30px"
                            customStyleObject={{ justifyContent: "center" }}
                            actionButton={<Button
                                content="Show Collection"
                                color="#fff"
                                backgroundColor="#ff7070"
                                borderRadius="30px"
                                height="50px"
                                width="150px"
                            />
                            }
                        />
                        <Card
                            height="375px"
                            width="350px"
                            title="Jackets"
                            subTitle="$ 39.99"
                            backgroundColor="purple"
                            color="#fff"
                            fontSize="30px"
                            customStyleObject={{ justifyContent: "center" }}
                            actionButton={<Button
                                content="Show All"
                                color="#fff"
                                backgroundColor="#ff7070"
                                borderRadius="30px"
                                height="50px"
                                width="150px"
                            />
                            }
                        />
                        <Card
                            height="375px"
                            width="350px"
                            title="Sale This Winter -50%"
                            backgroundColor="purple"
                            color="#fff"
                            fontSize="30px"
                            customStyleObject={{ justifyContent: "center" }}
                        />
                    </div>
                    <div className="popular">
                        <div className="popular-header">
                            <div className="popular-title">Popular</div>
                            <Button
                                content="Show More"
                                color="#fff"
                                backgroundColor="#ff7070"
                                borderRadius="30px"
                                height="50px"
                                width="120px"
                            />
                        </div>
                        <Carousel
                            className="carousel"
                            height="500px"
                            slideWidth="340px"
                            cellSpacing={45}
                            wrapAround={false}
                            renderCenterLeftControls={previousButton}
                            renderCenterRightControls={nextButtom}
                            renderBottomCenterControls={(): any => { }}
                        >
                            {[0, 1, 2, 3, 4].map((i) => (
                                <div className="card" key={i}>
                                    <Card
                                        height="375px"
                                        width="350px"
                                        backgroundColor="purple"
                                        color="#fff"
                                        fontSize="30px"
                                    />
                                    <div className="product-info">
                                        <div className="product-name">Jacket</div>
                                        <div className="product-price">
                                            <div className="new-price">$21.99</div>
                                            <div className="old-price" style={{
                                                textDecoration: "line-through",
                                            }}>$39.99</div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </Carousel>
                    </div>
                    <div className="pros-cards">
                        <div className="pros-header">Why you should choose us?</div>
                        <div className="pros-list">
                            {[<Icons.FreeShippingSmall />, <Icons.Wallet />, <Icons.Guarantee />].map((i) => (
                                <Card
                                    header={<div className="pros-card-icon">{i}</div>}
                                    height="250px"
                                    width="450px"
                                    title="Free Shipping"
                                    subTitle={
                                        <div className="pros-card-subtitle">
                                            All purchases over $199 are eligible for free shipping
                                        </div>
                                    }
                                    color="#000"
                                    textPosition="left"
                                    border={true}
                                />
                            ))}
                        </div>
                    </div>
                </div>
            </div>
            <style jsx>{`
                @font-face {
                    font-family: 'SegoeUIBold';
                    src: url('fonts/SegoeUI-Bold.ttf');
                }



                .header{
                    background: grey;
                    height: 750px
                }
                .navbar{
                    display: flex;
                    justify-content: space-between;
                    margin-left: 170px;
                    margin-right: 170px;
                    padding-top: 40px
                }
                .categories{
                    display: flex;
                    width: 100px;
                    justify-content: space-between;
                }
                .actions{
                    display: flex;
                    width: 120px;
                    justify-content: space-between;
                }
                .search{
                    cursor: pointer
                }
                .header-main{
                    margin-left: 210px;
                    margin-top: 80px
                }
                .title{
                    font-size: 50px;
                    width: 300px
                }
                .main{
                    margin-left: 170px;
                    margin-right: 170px;
                }
                .promocards{
                    margin-top: 40px;
                    display: flex;
                    justify-content: space-between
                }
                .popular-header{
                    margin-top: 120px;
                    display: flex;
                    justify-content: space-between;
                    margin-bottom: 60px;
                }
                .popular-title{
                    font-size: 29px
                }
                .product-info{
                    margin-top: 40px
                }
                .product-price {
                    width: 200px;
                    display: flex;
                    justify-content: space-between;
                    font-size: 16px;
                }
                .new-price{
                    font-size: 20px;
                    color: red;
                }
                .old-price{
                    font-size: 20px;
                    color: grey;
                }
                .pros-cards{
                    margin-top: 80px;
                }
                .pros-header{
                    text-align: center;
                    font-size: 29px;
                }
                .pros-list{
                    display: flex;
                    justify-content: space-between;
                    margin-top: 60px;
                    font-family: 'SegoeUIBold', serif;
                }
                .pros-card-icon{
                    height: 70px;
                    width: 70px;
                    border-radius: 6px;
                    background: #d9d9d9;
                    display: flex;
                    justify-content: center;
                    align-items: center
                }
                .pros-card-subtitle{
                    font-family: 'SegoeUI', serif;
                }
            `}</style>
        </>
    )
}
