import React, { ReactElement, useRef, useEffect, useState } from "react"
import Button from "./Button"

namespace CardNamespace {
    export interface Props {
        customStyleObject?: object
        children?: React.ReactNode
        onMouseEnter?: () => void
        onMouseLeave?: () => void
        backgroundColor?: string
        [propName: string]: any
        borderRadius?: string
        onClick?: () => void
        className?: string
        height?: string
        width?: string
        header?: string
        image?: { path: string, position: "top" | "bottom" | "left" | "right" }
        border?: boolean
        borderColor?: string
        bgImage?: string
        textPosition?: "center" | "left" | "right",
        title?: string
        subTitle?: string
        text?: string
        actionButton?: React.ReactNode
        footer: React.ReactNode
    }
}

export default function Card(props: CardNamespace.Props) {
    const cardRef: any = useRef()
    const [cardHeight, setHeight] = useState()

    useEffect(() => {
        setHeight(cardRef.current.getBoundingClientRect().height)
    }, [])

    const btnPosition = (textPosition: string) => {
        switch (textPosition) {
            case "center":
                return "center"
            case "left": 
                return "flex-start"
            case "right":
                return "flex-end"
            default:
                break
        }
    }

    const commonTextStyle = {
        textAlign: props.textPosition ? props.textPosition : "center",
        marginRight: props.textPosition === "right" ? "10px" : "",
        marginLeft: props.textPosition === "left" ? "10px" : "",
    }
    const headerStyle = {
        ...commonTextStyle,
        marginTop: "5px",
    }

    const titleStyle = {
        ...commonTextStyle,
        marginTop: "5px",
        fontSize: "16px",
    }

    const subTitleStyle = {
        ...commonTextStyle,
        fontSize: "14px",
        color: "gray",
    }

    const textStyle = {
        ...commonTextStyle,
        fontSize: "14px",
        marginTop: "5px",
    }

    const btnStyle = {
        marginTop: "10px",
        display: "flex",
        justifyContent: props.textPosition ? btnPosition(props.textPosition) : "center",
        marginRight: props.textPosition === "right" ? "10px" : "",
        marginLeft: props.textPosition === "left" ? "10px" : "",
        ...commonTextStyle,
    }

    const footerStyle = {
        borderTop: "1px solid gray",
        marginTop: "10px"
    }

    const imageStyle = {
        backgroundImage: props.image ? "url(" + props.image.path + ")" : "",
        backgroundPosition: "center center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        height: `${cardHeight / 2}px`,
    }

    const cardStyle = {
        minHeight: props.height ? props.height : "250px",
        width: props.width ? props.width : "200px",
        border: props.border ? `1px solid ${props.borderColor ? props.borderColor : "grey"}` : "none",
        backgroundImage: props.bgImage ? "url(" + props.bgImage + ")" : "",
        backgroundPosition: "center center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        backgroundColor: props.backgroundColor ? props.backgroundColor : "#fff",
        borderRadius: props.borderRadius ? props.borderRadius : "3px",
        paddingBottom: "10px",
    }

    return (
        <div ref={cardRef} style={cardStyle}>
            {props.header ? <div style={headerStyle}>{props.header}</div> : ""}
            {props.image ? <div style={imageStyle} /> : ""}
            {props.title ? <div style={titleStyle}>{props.title}</div> : ""}
            {props.subTitle ? <div style={subTitleStyle}>{props.subTitle}</div> : ""}
            {props.text ? <div style={textStyle}>{props.text}</div> : ""}
            {props.actionButton ?  <div style={btnStyle}>{props.actionButton}</div> : ""}
            {props.footer ?  <div style={footerStyle}>{props.footer}</div> : ""}
        </div>
    )
}
