import React from 'react'

namespace Btn {
    export interface Props {
        children?: React.ReactNode
        onMouseEnter?: () => void
        onMouseLeave?: () => void
        backgroundColor?: string
        [propName: string]: any
        size?: "big" | "small"
        borderRadius?: string
        onClick?: () => void
        focusable?: boolean
        className?: string
        fontSize?: string
        content?: string
        height?: string
        color?: string
        width?: string
        base?: boolean
    }
}

export default function Button(props: Btn.Props) {

    return (
        <>
            <div
                className={props.className + " btn-main"}
                onClick={props.onClick}
                onMouseEnter={props.onMouseEnter}
                onMouseLeave={props.onMouseLeave}
            >
                {props.content}
            </div>
            <style jsx>{`
                .btn-main{
                    height: ${props.height ? props.height : "40px"};
                    width: ${props.width ? props.width : "150px"};
                    font-size: ${props.fontSize ? props.fontSize : "14px"};
                    background: ${props.backgroundColor ? `${props.base ? "transaprent" : props.backgroundColor}` : `${props.base ? "transaprent" : "grey"}`};
                    border: ${props.base ? `1px solid ${props.backgroundColor ? props.backgroundColor : "grey"}`: "none"};
                    border-radius: ${props.borderRadius ? props.borderRadius : "4px"};
                    color: background-color: ${props.color ? props.color : "black"};
                    cursor: pointer;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                }
                .btn-main:focus{
                    background: #fff;
                }
            `}</style>
        </>
    )
}
