import React from "react"
import styled from "styled-components"

namespace Styles {
    export const Background = (props: Btn.Props) => props.backgroundColor ? `${props.base ? "transaprent" : props.backgroundColor}` : `${props.base ? "transaprent" : "grey"}`
    export const Border = ({ base, backgroundColor }: Btn.Props) => base ? `1px solid ${backgroundColor ? backgroundColor : "grey"}` : "none"
    export const BorderRadius = ({ borderRadius }: Btn.Props) => borderRadius ? borderRadius : "4px"
    export const FontSize = ({ fontSize }: Btn.Props) => fontSize ? fontSize : "14px"
    export const Height = ({ height }: Btn.Props) => height ? height : "40px"
    export const Width = ({ width }: Btn.Props) => width ? width : "150px"
    export const Color = ({ color }: Btn.Props) => color ? color : "black"
}

namespace Btn {
    export interface Props {
        children?: React.ReactNode
        onMouseEnter?: () => void
        onMouseLeave?: () => void
        backgroundColor?: string
        [propName: string]: any
        size?: "big" | "small"
        borderRadius?: string
        onClick?: () => void
        focusable?: boolean
        className?: string
        fontSize?: string
        content?: string
        height?: string
        color?: string
        width?: string
        base?: boolean
    }
}

const BtnMain = styled.div`
    height: ${Styles.Height};
    width: ${Styles.Width};
    font-size: ${Styles.FontSize};
    background: ${Styles.Background};
    border: ${Styles.Border};
    border-radius: ${Styles.BorderRadius};
    color: ${Styles.Color};
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;
`

export default function Button(props: Btn.Props) {
    console.log(Styles.Background(props))
    return (
        <BtnMain
            onClick={props.onClick}
            onMouseEnter={props.onMouseEnter}
            onMouseLeave={props.onMouseLeave}
        >
            {props.content}
        </BtnMain>
    )
}
